Interactive in Shiny

Basic Setup
library(shiny)
library(ggiraph)
library(cpaltemplates)

ui &lt;- fluidPage(
  theme = cpal_dashboard_theme(),
  girafeOutput("interactive_plot")
)

server &lt;- function(input, output) {
  output$interactive_plot &lt;- renderGirafe({
    p &lt;- ggplot(mtcars, aes(x = wt, y = mpg, color = factor(cyl))) +
      geom_point_interactive(aes(tooltip = rownames(mtcars))) +
      scale_color_cpal("main_3") +
      theme_cpal()

    cpal_interactive(p)
  })
}

shinyApp(ui, server)


With Selection
server &lt;- function(input, output) {
  output$interactive_plot &lt;- renderGirafe({
    mtcars$id &lt;- rownames(mtcars)

    p &lt;- ggplot(mtcars, aes(x = wt, y = mpg)) +
      geom_point_interactive(
        aes(tooltip = id, data_id = id),
        size = 3,
        color = "#007A8C"
      ) +
      theme_cpal()

    girafe(
      ggobj = p,
      options = list(
        opts_selection(type = "single", css = "fill:#C3257B;stroke:#C3257B;")
      )
    )
  })

  output$selected &lt;- renderText({
    paste("Selected:", input$interactive_plot_selected)
  })
}

Quick Start
library(shiny)
library(bslib)
library(cpaltemplates)

ui &lt;- page_sidebar(
  theme = cpal_dashboard_theme(),
  title = "CPAL Dashboard",
  sidebar = sidebar(
    selectInput("var", "Variable", choices = names(mtcars))
  ),
  card(
    card_header("Plot"),
    plotOutput("plot")
  )
)

server &lt;- function(input, output) {
  output$plot &lt;- renderPlot({
    ggplot(mtcars, aes(x = .data[[input$var]])) +
      geom_histogram(fill = "#007A8C") +
      theme_cpal()
  })
}

shinyApp(ui, server)
